
import express from 'express';
import axios from 'axios';
import cheerio from 'cheerio';
const app = express();
const PORT = process.env.PORT || 3000;

async function pinterest(query) {
  return new Promise(async (resolve, reject) => {
    try {
      const { data } = await axios.get(`https://id.pinterest.com/search/pins/?autologin=true&q=${query}`, {
        headers: {
          "cookie": "_auth=1; _b=\"AVna7S1p7l1C5I9u0+nR3YzijpvXOPc6d09SyCzO+DcwpersQH36SmGiYfymBKhZcGg=\"; _pinterest_",
        }
      });

      const $ = cheerio.load(data);
      const result = [];
      const get = [];

      $('div > a').get().map(b => {
        const link = $(b).find('img').attr('src');
        result.push(link);
      });

      result.forEach(v => {
        if (v == undefined) return;
        get.push(v.replace(/236/g, '736'));
      });

      get.shift();
      resolve(get);
    } catch (error) {
      reject(error);
    }
  });
}

app.get('/pinterest', async (req, res) => {
  try {
    const query = req.query.q;
    if (!query) {
      return res.status(400).json({ error: 'Missing query parameter "q"' });
    }

    const urls = Array.from(new Set(await pinterest(query)));
    res.json({ urls });
  } catch (error) {
    res.status(500).json({ error: 'Internal Server Error' });
  }
});

app.listen(PORT, () => {
  console.log(`Server is running on port ${PORT}`);
});
